changeSet.createProcedure:
CREATE OR REPLACE FUNCTION fn_create_tenant()
RETURNS TRIGGER
LANGUAGE plpgsql
AS $fn$
DECLARE
pschemaname VARCHAR default TG_TABLE_SCHEMA;
ptenantid VARCHAR default new.tenant_id;
lRec record;
strPartTblName VARCHAR DEFAULT ' ';
strTempTblName VARCHAR DEFAULT ' ';
strPrevSchema VARCHAR DEFAULT ' ';
BEGIN
-- RAISE INFO 'passed values are %I %I',pschemaname,ptenantid;
-- preserve the current schema before changing it
SELECT current_schemas(false) into strPrevSchema;
-- set the current search to tmp_{given schema} this will be scrach pad for the latest prod schema tables
EXECUTE 'SET search_path TO '|| pschemaname;
-- clean any old prod tables info if exist
FOR lRec IN EXECUTE FORMAT('select par.relname from  (select partrelid from pg_partitioned_table) pt join  pg_class par on   par.oid = pt.partrelid where par.relnamespace::regnamespace::text = %s', quote_literal( pschemaname))
LOOP
strPartTblName = CONCAT(lRec.relname,'_',REPLACE(pTenantID,'-','_')) ;
EXECUTE FORMAT('SELECT table_name FROM information_schema.tables WHERE table_schema = %s AND table_name = %s', quote_literal(pschemaname),  quote_literal(strPartTblName)) INTO strTempTblName;
IF (strTempTblName IS NULL OR strTempTblName = '') THEN
EXECUTE FORMAT('CREATE TABLE %s PARTITION OF %s FOR VALUES IN  (%s)', strPartTblName, lRec.relname, quote_literal(pTenantID));
END IF;
END LOOP;
EXECUTE 'SET search_path TO '|| REPLACE(REPLACE(strPrevSchema,'{',''),'}','');
RETURN NULL;
EXCEPTION
WHEN OTHERS THEN
GET STACKED DIAGNOSTICS strPartTblName = PG_EXCEPTION_CONTEXT;
RAISE EXCEPTION USING
MESSAGE = FORMAT('unexpected error  %I ERROR: %s', SQLERRM, strPartTblName),
HINT = 'Fix the error',
ERRCODE = SQLSTATE;
RETURN NULL;
END;
$fn$;

CREATE OR REPLACE FUNCTION fn_get_account_type(pAccountID UUID) RETURNS ENM_ACCT_TYPE AS
$getActType$
DECLARE
actType ENM_ACCT_TYPE;
BEGIN
SELECT account_type INTO actType FROM tbl_chart_accounts WHERE  chart_accounts_id = pAccountID;
RETURN actType;
END;
$getActType$ LANGUAGE plpgsql;

CREATE FUNCTION tr_fn_chk_act_type() RETURNS TRIGGER AS
$checkActType$
BEGIN
IF 'SALE'::ENM_ACCT_TYPE != fn_get_account_type(NEW.over_allowance_chart_accounts_fk) THEN
RAISE EXCEPTION 'over_allowance_chart_accounts % Is not type of SALE', NEW.over_allowance_chart_accounts_fk;
END IF;
IF 'COST'::ENM_ACCT_TYPE != fn_get_account_type(NEW.cost_account_chart_accounts_fk) THEN
RAISE EXCEPTION 'cost_account_chart_accounts % Is not type of COST', NEW.cost_account_chart_accounts_fk;
END IF;
IF 'COST'::ENM_ACCT_TYPE != fn_get_account_type(NEW.recon_account_chart_accounts_fk) THEN
RAISE EXCEPTION 'recon_account_chart_accounts % Is not type of COST', NEW.recon_account_chart_accounts_fk;
END IF;
IF fn_get_account_type(NEW.cost_offset_account_chart_accounts_fk) IN ('ASSET'::ENM_ACCT_TYPE,'LIABILITY'::ENM_ACCT_TYPE)  THEN
RAISE EXCEPTION 'cost_offset_account_chart_accounts % Is not type of COST', NEW.cost_offset_account_chart_accounts_fk;
END IF;
RETURN NEW;
END
$checkActType$ LANGUAGE 'plpgsql';
